/* eslint react/prop-types: 0 */
import './App.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Run the json server : npx json-server --port=3001 --watch names.json

const RenderPerson = (person) => (
  <tr>
    <td>{person.name}</td>
    <td>{person.amount}</td>
  </tr>
);

const RenderPeople = ({ people }) => (
  <table>
    <tr>
      <th>NAME</th>
      <th>NUMBER</th>
    </tr>
    {people.map((person) => <RenderPerson person={person} />)}
  </table>
);

const App = () => {
  // const [person, setPerson] = useState('');
  const [people, setPeople] = useState('');

  useEffect(async () => {
    const persons = await axios.get('http://localhost:3001/names');
    setPeople(persons.data);
  }, []);

  // eslint-disable-next-line
  //console.log('people :>> ', typeOf(people));

  return (
    <RenderPeople people={people} />
  );
};

export default App;
